var ShapeOverlays=function(a){this.elm=a;this.path=a.querySelectorAll("path");this.numPoints=4;this.duration=800;this.delayPointsArray=[];this.delayPointsMax=180;this.delayPerPath=70;this.timeStart=Date.now();this.isAnimating=this.isOpened=!1};ShapeOverlays.prototype.toggle=function(){this.isAnimating=!0;for(var a=Math.random()*Math.PI*2,c=0;c<this.numPoints;c++)this.delayPointsArray[c]=(Math.sin(c/(this.numPoints-1)*Math.PI*2+a)+1)/2*this.delayPointsMax;!1===this.isOpened?this.open():this.close()};
ShapeOverlays.prototype.open=function(){this.isOpened=!0;this.elm.classList.add("is-opened");this.timeStart=Date.now();this.renderLoop()};ShapeOverlays.prototype.close=function(){this.isOpened=!1;this.elm.classList.remove("is-opened");this.timeStart=Date.now();this.renderLoop()};
ShapeOverlays.prototype.updatePath=function(a){for(var c=[],b=0;b<this.numPoints;b++)c[b]=100*ease.cubicInOut(Math.min(Math.max(a-this.delayPointsArray[b],0)/this.duration,1));a=this.isOpened?"M 0 0 V "+c[0]+" ":"M 0 "+c[0]+" ";for(b=0;b<this.numPoints-1;b++){var d=(b+1)/(this.numPoints-1)*100,e=d-1/(this.numPoints-1)*100/2;a+="C "+e+" "+c[b]+" "+e+" "+c[b+1]+" "+d+" "+c[b+1]+" "}return a+=this.isOpened?"V 0 H 0":"V 100 H 0"};
ShapeOverlays.prototype.render=function(){if(this.isOpened)for(var a=0;a<this.path.length;a++)this.path[a].setAttribute("d",this.updatePath(Date.now()-(this.timeStart+this.delayPerPath*a)));else for(a=0;a<this.path.length;a++)this.path[a].setAttribute("d",this.updatePath(Date.now()-(this.timeStart+this.delayPerPath*(this.path.length-a-1))))};
ShapeOverlays.prototype.renderLoop=function(){var a=this;this.render();Date.now()-this.timeStart<this.duration+this.delayPerPath*(this.path.length-1)+this.delayPointsMax?requestAnimationFrame(function(){a.renderLoop()}):this.isAnimating=!1};
